-- Dumping data for table `story`
--

INSERT INTO `story` (`id_story`, `image`, `name_story`, `name_story_unsigned`, `chapter`, `author`, `source`, `status`, `introduce`, `evaluate`, `created_at`, `updated_at`) VALUES
(60, 'toi-cuong-he-thong-tan-phong.jpg', 'TỐI CƯỜNG HỆ THỐNG', 'toi-cuong-he-thong', 2, 'Tân Phong', 'Bạch Ngọc Sách, 4vn, iRead', '1', '<p><strong>Thể loại:</strong>&nbsp;Ti&ecirc;n Hiệp, H&agrave;i Hước<br />\r\n<strong>Ph&oacute;ng t&aacute;c:</strong>&nbsp;&dagger;Ares&dagger;<br />\r\n<strong>Nh&oacute;m dịch:&nbsp;</strong>Vạn Y&ecirc;n Chi S&agrave;o<br />\r\n<br />\r\nTruyện cực kỳ h&agrave;i, cực kỳ bựa nhưng đ&ocirc;i l&uacute;c cũng rất t&igrave;nh cảm nhẹ nh&agrave;ng xen lẫn với bi tr&aacute;ng.<br />\r\n<br />\r\nTruyện kh&ocirc;ng tồn tại ngựa giống c&agrave;ng kh&ocirc;ng c&oacute; &quot;tất cả đều thuộc về nh&acirc;n vật ch&iacute;nh&quot;<br />\r\n<br />\r\nNgươi giống những nh&acirc;n vật trong tiểu thuyết? Kh&ocirc;ng sao hết. Ca c&oacute; &quot;hệ thống bỉ ổi&quot; c&oacute; thể c&acirc;n cả thi&ecirc;n hạ n&agrave;y.<br />\r\n&nbsp;</p>\r\n', 0, '2023-05-30 09:23:07', '2023-05-30 09:23:07'),
(61, 'bao-thu-xuyen-khong-zhihu.jpg', 'BÁO THỦ XUYÊN KHÔNG', 'bao-thu-xuyen-khong', 1, 'Zhihu', 'toptruyen.com', '3', '<p>[TRUYỆN NGẮN] B&Aacute;O THỦ XUY&Ecirc;N KH&Ocirc;NG<br />\r\nT&aacute;c giả: 海的鸽子<br />\r\nEdit: Ryan<br />\r\n______________<br />\r\n<br />\r\nGiới thiệu truyện:<br />\r\n<br />\r\nT&ocirc;i xuy&ecirc;n v&agrave;o cơ thể của một nữ sinh bị bạo lực học đường, t&ecirc;n nam sinh mặt m&agrave;y s&aacute;ng sủa trước mặt đang t&uacute;m lấy t&oacute;c t&ocirc;i, muốn d&iacute; đầu thuốc l&aacute; đang ch&aacute;y l&ecirc;n vai t&ocirc;i.<br />\r\n<br />\r\nE h&egrave;m, t&ocirc;i l&agrave; nh&agrave; v&ocirc; địch quyền anh năm nay đấy.<br />\r\n<br />\r\nT&ocirc;i đấm một ph&aacute;t khiến cậu ta ng&atilde; lăn ra đất, d&iacute; thẳng đầu thuốc gần tắt l&ecirc;n c&aacute;i m&aacute; trắng n&otilde;n của cậu ta, sau đ&oacute; mặt v&ocirc; cảm vỗ vỗ m&aacute; cậu ta:<br />\r\n<br />\r\n&ldquo;Nhớ kĩ nh&eacute; thằng ch&aacute;u, từ h&ocirc;m nay trở đi tao sẽ l&agrave; &ocirc;ng nội m&agrave;y!&rdquo;</p>\r\n', 0, '2023-05-31 05:01:22', '2023-05-31 05:01:22'),
(62, 'than-the-toi-bi-xuyen-qua-san-ho-thu.jpg', 'THÂN THỂ TÔI BỊ XUYÊN QUA', 'than-the-toi-bi-xuyen-qua', 2, 'San Hô Thụ', 'toptruyen.com', '1', '<p>L&agrave;m nữ chủ sảng văn, Khương Mạt vốn n&ecirc;n thu nhận đ&agrave;n em, l&agrave;m l&atilde;o đại, gả cho đ&agrave;n &ocirc;ng ba tốt &ldquo;cao, ph&uacute;, so&aacute;i&rdquo;, đi tr&ecirc;n đỉnh cao của nh&acirc;n sinh.<br />\r\n<br />\r\nKh&ocirc;ng ngờ năm mười t&aacute;m tuổi lại bị xuy&ecirc;n, mười năm sau tỉnh lại trở th&agrave;nh nữ minh tinh bị b&ocirc;i đen tr&ecirc;n khắp mạng x&atilde; hội, vừa vướng v&agrave;o scandal ngoại t&igrave;nh, &ocirc;ng chồng đại ph&uacute; h&agrave;o cũng gi&agrave;u kh&ocirc;ng nổi, đừng n&oacute;i đến kiếm tiền nu&ocirc;i gia đ&igrave;nh, đến nh&agrave; c&ograve;n phải ở nh&agrave; g&aacute;i&hellip;.. Khương Mạt tay cầm giấy ly h&ocirc;n g&otilde; cửa nh&agrave; m&igrave;nh, người mở cửa mang gương mặt của người m&agrave; Khương Mạt y&ecirc;u thầm năm cấp ba&hellip;&hellip;!<br />\r\n<br />\r\nKhương Mạt: &ldquo;Kh&ocirc;ng ly h&ocirc;n nữa! Kh&ocirc;ng ly h&ocirc;n nữa! C&aacute;i nh&agrave; n&agrave;y t&ocirc;i nu&ocirc;i!&rdquo;<br />\r\n<br />\r\nKhương Mạt đưa cho chồng một tấm thẻ ng&acirc;n h&agrave;ng: &ldquo;Muốn mua g&igrave;, t&ugrave;y anh quẹt!&rdquo;<br />\r\n<br />\r\nSau một thời gian d&agrave;i, c&ocirc; chợt nổi l&ecirc;n &yacute; nghĩ kiểm tra số dư trong t&agrave;i khoản&hellip;&hellip;&hellip; &Ocirc;i đệch, từ khi n&agrave;o b&agrave; đ&acirc;y lại c&oacute; nhiều tiền thế n&agrave;y?!<br />\r\n<br />\r\nCp: Thiếu nữ nội t&acirc;m mười t&aacute;m tuổi nu&ocirc;i gia đ&igrave;nh X đệ nhất ph&uacute; h&agrave;o ẩn h&igrave;nh hai mươi t&aacute;m tuổi</p>\r\n', 0, '2023-05-31 04:57:08', '2023-05-31 04:57:08'),
(63, 've-sau-mua-ha-zhihu.jpg', 'VE SẦU MÙA HẠ', 've-sau-mua-ha', 2, 'Zhihu', 'Hoa sơn chi không muốn nở', '3', '<p>Năm 17 tuổi, t&ocirc;i tỏ t&igrave;nh với anh trước to&agrave;n trường.<br />\r\n<br />\r\nKhi đ&oacute; anh lu&ocirc;n cau m&agrave;y từ chối t&ocirc;i.<br />\r\n<br />\r\nNhưng năm 24 tuổi, ngay cả dũng kh&iacute; nh&igrave;n anh t&ocirc;i cũng kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nAnh vẫn cau m&agrave;y, hỏi t&ocirc;i: &ldquo;Chu Mi&ecirc;n của anh, thế giới đ&atilde; bắt nạt em như thế n&agrave;o vậy?&rdquo;</p>\r\n', 0, '2023-05-31 06:48:52', '2023-05-31 06:48:52'),
(64, 'hanh-phuc-la-khi-gap-duoc-em-hoang-kim-(aiko).jpg', 'HẠNH PHÚC LÀ KHI GẶP ĐƯỢC EM', 'hanh-phuc-la-khi-gap-duoc-em', 3, 'Hoàng Kim (Aiko)', 'truyenfull.vn', '3', '<p>T&aacute;c giả: Ho&agrave;ng Kim (Aiko)<br />\r\n<br />\r\nThể loại: Đ&ocirc; Thị, Truyện Teen, Ng&ocirc;n T&igrave;nh<br />\r\n<br />\r\nGiới thiệu:<br />\r\n<br />\r\nNam ch&iacute;nh: Dương Thừa Nam (25 tuổi)<br />\r\n<br />\r\nNữ ch&iacute;nh: Diệp Khả Như (20 tuổi)<br />\r\n<br />\r\nAnh v&agrave; c&ocirc; gặp nhau khi cả hai vẫn chỉ l&agrave; những đứa trẻ.<br />\r\n<br />\r\nLần đầu gặp nhau l&agrave; khi anh nh&igrave;n thấy c&ocirc; ngồi thẫn thờ với đ&ocirc;i mắt đỏ hoe b&ecirc;n ngo&agrave;i cửa h&agrave;ng tạp h&oacute;a. Trong l&ograve;ng anh l&uacute;c ấy bỗng dưng l&ecirc;n cảm x&uacute;c muốn bảo vệ v&agrave; che chở.<br />\r\n<br />\r\nKhẽ tiến lại gần c&ocirc; g&aacute;i nhỏ. Tiểu Thừa Nam mỉm cười tươi rối rồi cất tiếng:<br />\r\n<br />\r\n- Nh&oacute;c con, em c&oacute; chuyện g&igrave; buồn hả?<br />\r\n<br />\r\nC&ocirc; b&eacute; với m&aacute;i t&oacute;c đen b&ugrave; x&ugrave; ngang vai th&uacute;t th&iacute;t v&agrave;i tiếng sau đ&oacute; lấy b&agrave;n tay b&eacute; nhỏ d&iacute;nh đầy bụi bẩn chạm l&ecirc;n khu&ocirc;n mặt rồi từ từ trả lời:<br />\r\n<br />\r\n- E...em đ&oacute;i.... em nhớ mẹ...<br />\r\n<br />\r\nC&agrave;ng n&oacute;i về sau giọng n&oacute;i c&agrave;ng nhỏ đi k&egrave;m theo tiếng nức nở vang vọng.<br />\r\n<br />\r\nTiểu Thừa Nam kh&ocirc;ng biết l&agrave;m g&igrave;, tay ch&acirc;n luống cuống ngồi xuống trước mặt c&ocirc; b&eacute; rồi đưa tay gi&uacute;p c&ocirc; lau nước mắt.<br />\r\n<br />\r\n- Ngoan, đừng kh&oacute;c nữa! Anh n&oacute;i mẹ mua đồ ăn cho em nha!<br />\r\n<br />\r\n- Th...thật kh&ocirc;ng ạ?<br />\r\n<br />\r\nC&ocirc; b&eacute; ng&acirc;y ng&ocirc; nh&igrave;n người trước mặt, &aacute;nh mắt mang vẻ mong chờ v&agrave; kỳ vọng.<br />\r\n<br />\r\n- Thật m&agrave;! &Agrave; phải rồi, em t&ecirc;n l&agrave; g&igrave; vậy?<br />\r\n<br />\r\n- Em...em t&ecirc;n l&agrave; Khả Như!</p>\r\n', 0, '2023-05-31 05:48:19', '2023-05-31 05:48:19'),
(65, 'o-tan-the-ta-xay-dung-thi-tran-nho-nguyet-ban-dinh.jpg', 'Ở TẬN THẾ TA XÂY DỰNG THỊ TRẤN NHỎ', 'o-tan-the-ta-xay-dung-thi-tran-nho', 1, 'Nguyệt Bán Đinh', 'truyenfull.vn', '2', '<p>T&aacute;c giả: Nguyệt B&aacute;n Đinh<br />\r\n<br />\r\nThể loại: Điền Văn, Dị Giới, Xuy&ecirc;n Kh&ocirc;ng, Mạt Thế, Huyền huyễn<br />\r\n<br />\r\nGiới thiệu:<br />\r\n<br />\r\nCuộc sống trong những ng&agrave;y tận thế l&agrave; như n&agrave;o?<br />\r\n<br />\r\nTrả lời: Xin mời t&igrave;m hiểu, mỗi ng&agrave;y ngủ đến th&iacute;ch th&igrave; dậy, một ng&agrave;y ăn đủ ba bữa cơm, thoải m&aacute;i tập thể h&igrave;nh hay dắt ch&oacute; m&egrave;o đi dạo, đi mua sắm ở si&ecirc;u thị, c&ograve;n đi xem phim.<br />\r\n<br />\r\nĐ&uacute;ng rồi, dạo gần đ&acirc;y trấn trưởng của ch&uacute;ng ta c&ograve;n x&acirc;y th&ecirc;m một c&aacute;i vườn b&aacute;ch th&uacute;, cuối tuần c&ograve;n c&oacute; thể đưa con c&aacute;i đi chơi, ngo&agrave;i việc đi kiếm tiền để trả tiền thu&ecirc; nh&agrave; chẳng ai nguyện &yacute; đi ra khỏi nơi n&agrave;y.<br />\r\n<br />\r\nC&oacute; người hỏi ở đ&acirc;y c&oacute; m&acirc;u thuẫn sao? L&agrave;m g&igrave; c&oacute; chuyện đ&oacute;, th&ocirc;i kh&ocirc;ng n&oacute;i nữa, t&ocirc;i c&ograve;n phải đi đ&oacute;n con tan học, buổi tối c&ograve;n phải ngồi xe lửa đi thăm ch&uacute; hai, tiện thể c&ograve;n đ&oacute;n ch&uacute; ấy đến đ&acirc;y chơi v&agrave;i ng&agrave;y.<br />\r\n<br />\r\n&hellip;&hellip;<br />\r\n<br />\r\nV&agrave;o năm thứ năm từ khi tận thế bắt đầu, bỗng nhi&ecirc;n xuất hiện một trấn nhỏ thần b&iacute; ở th&agrave;nh phố H trống rỗng n&agrave;y<br />\r\n<br />\r\nTrong trấn nhỏ c&oacute; ph&ograve;ng ngủ ấm &aacute;p, c&oacute; đồ ăn đầy đủ, c&oacute; nước sạch sẽ d&ugrave;ng để uống, c&ograve;n c&oacute; đ&egrave;n điện s&aacute;ng ch&oacute;i v&agrave; ban đ&ecirc;m v&agrave; an ninh cực k&igrave; tốt.<br />\r\n<br />\r\nTheo sự ph&aacute;t triển của trấn nhỏ, người d&acirc;n trong trấn c&agrave;ng ng&agrave;y c&agrave;ng nhiều, phương tiện ở b&ecirc;n trong trấn cũng c&agrave;ng ng&agrave;y c&agrave;ng phong ph&uacute;.<br />\r\n<br />\r\nN&ocirc;ng nghiệp, c&ocirc;ng nghiệp v&agrave; gi&aacute;o dục ph&aacute;t triển rất mạnh mẽ, cuối c&ugrave;ng c&ograve;n x&acirc;y được đường ray xe lửa!</p>\r\n', 0, '2023-05-31 05:54:07', '2023-05-31 05:54:07'),
(66, 'xuyen-ve-bo-lac-nguyen-thuy-lam-thu-linh-that-doan-mao-nhung-nhung.jpg', 'XUYÊN VỀ BỘ LẠC NGUYÊN THỦY LÀM THỦ LĨNH', 'xuyen-ve-bo-lac-nguyen-thuy-lam-thu-linh', 2, 'Thất Đoàn Mao Nhung Nhung', 'truyenfull.vn', '3', '<p>Giới thiệu:<br />\r\n<br />\r\nThời điểm Qu&acirc;n Tiểu Nam vừa rời khỏi nơi l&agrave;m việc liền xuy&ecirc;n th&agrave;nh tiểu d&atilde; nh&acirc;n.<br />\r\n<br />\r\nCha mẹ thương y&ecirc;u chiều chuộng, nhưng c&aacute;c tộc nh&acirc;n sinh sống xung quanh c&oacute; điểm kỳ qu&aacute;i.<br />\r\n<br />\r\nNg&agrave;y h&egrave; động thực vật phong ph&uacute; mọi người liền săn th&uacute; lấy thịt sau đ&oacute; l&agrave;m thịt kh&ocirc;; đến ng&agrave;y đ&ocirc;ng r&eacute;t lạnh, tuyết rơi kh&ocirc;ng ngừng th&igrave; mọi người liền tr&aacute;nh trong sơn động nhai thịt kh&ocirc;, ăn ngủ đến đờ người&nbsp;&nbsp;<br />\r\n<br />\r\nQu&acirc;n Tiểu Nam: A a a ch&uacute;ng ta l&agrave; một team! Ch&uacute;ng ta phải c&ugrave;ng nhau l&agrave;m ruộng, x&acirc;y dựng th&ocirc;n x&oacute;m, th&agrave;nh lập vương quốc!<br />\r\n<br />\r\nC&aacute;c tộc nh&acirc;n:???<br />\r\n<br />\r\nQu&acirc;n Tiểu Nam: May mắn l&agrave; địa điểm c&ocirc; l&agrave;m c&ocirc;ng cũng c&ugrave;ng nhau xuy&ecirc;n qua, c&ograve;n c&oacute; thể v&ocirc; hạn bổ h&agrave;ng.<br />\r\n<br />\r\nM&ugrave;a xu&acirc;n năm nay, ở đại hội t&igrave;m phối ngẫu, c&aacute;c bộ lạc kh&aacute;c mặc tr&ecirc;n người những bộ da th&uacute; đầy rận, v&igrave; sao bộ lạc kia lại mặc thứ đồ vật được gọi l&agrave; [vải]!<br />\r\n<br />\r\nBọn họ cắt ngắn l&ocirc;ng t&oacute;c tr&ecirc;n đầu, thoạt nh&igrave;n d&aacute;ng vẻ rất phong độ, c&ograve;n đội mũ nhỏ! Dựa v&agrave;o c&aacute;i g&igrave;! Ta cũng muốn!</p>\r\n', 0, '2023-05-31 06:49:06', '2023-05-31 06:49:06'),
(67, 'than-kiem-phuc-quoc-dang-nhan.jpg', 'THẦN KIẾM PHỤC QUỐC', 'than-kiem-phuc-quoc', 1, 'Đăng Nhân', 'truyenfull.vn', '1', '<p>Bạn đang thưởng thức c&acirc;u chuyện Thần Kiếm Phục Quốc của t&aacute;c giả Đăng Nh&acirc;n, một sự kết hợp đầy kịch t&iacute;nh giữa kiếm hiệp, qu&acirc;n sự v&agrave; lịch sử Trung Quốc.<br />\r\n<br />\r\nKim triều tự cho rằng đang l&agrave; chủ nh&acirc;n của Trung Nguy&ecirc;n, tuy nhi&ecirc;n, mọi triều đại đều sẽ kết trong suy t&agrave;n v&agrave; tuyệt diệt. Nguy hiểm lớn nhất m&agrave; họ đang phải đối mặt l&agrave; sự dũng m&atilde;nh của người Hung N&ocirc; chiến đấu tại phương Bắc.<br />\r\n<br />\r\nVới đội qu&acirc;n v&ocirc; địch, Th&agrave;nh C&aacute;t Tư H&atilde;n đ&aacute;nh bại mọi đối thủ, khiến vua Tuy&ecirc;n T&ocirc;ng hoang mang v&agrave; phải l&ograve;ng ra khỏi kinh đ&ocirc;. Quyền lực nay tập trung trong tay th&aacute;i tử Thủ Trung, người t&agrave;n &aacute;c v&agrave; cực kỳ nguy hiểm. Khi kim triều ch&igrave;m v&agrave;o tuyệt vong, Tuy&ecirc;n T&ocirc;ng cử con g&aacute;i để đưa tin về Thủ Tự, người anh trai xứng đ&aacute;ng với vị tr&iacute; n&agrave;y. Với sự gi&uacute;p đỡ của Ho&agrave;n Thừa L&acirc;n, một thiếu ni&ecirc;n t&agrave;i ba c&oacute; t&agrave;i điều binh thượng tướng, họ đ&atilde; đ&aacute;nh bại đội qu&acirc;n của Th&agrave;nh C&aacute;t Tư H&atilde;n v&agrave; c&aacute;c v&otilde; sĩ t&agrave;i ba từ ph&aacute;i Ni Kham, D&atilde; Nh&acirc;n v&agrave; Nga quốc. Tuy nhi&ecirc;n, khi Th&agrave;nh C&aacute;t Tư H&atilde;n xuất trận, họ mới nhận ra sức mạnh v&agrave; t&agrave;i năng thực sự của Khả h&atilde;n.</p>\r\n', 0, '2023-05-31 06:26:09', '2023-05-31 06:26:09'),
(68, 'than-con-tien-nu-dai-nhan-cuong-cuoi-my-kieu-the-truong-cung-moc-moi-dieu.jpg', 'THẦN CÔN TIÊN NỮ ĐẠI NHÂN CƯỜNG CƯỚI MỸ KIỀU THÊ', 'than-con-tien-nu-dai-nhan-cuong-cuoi-my-kieu-the', 1, 'Trường Cung Mộc Mỗi Điểu', 'truyenfull.vn', '1', '<p>Đối với Đại L&yacute; Tự Khanh - Địch Qu&acirc;n Dương trẻ tuổi t&agrave;i giỏi anh tuấn m&agrave; n&oacute;i, vị h&ocirc;n th&ecirc; Hỉ Dương n&agrave;y từ tr&ecirc;n xuống dưới đều tr&agrave;n ngập kh&iacute; chất m&ecirc; hoặc người kh&aacute;c, đặc biệt l&agrave;&hellip; đ&ocirc;i b&agrave;n ch&acirc;n&hellip; b&oacute; nhỏ xinh xinh kia của n&agrave;ng, đặc biệt ph&ugrave; hợp với sở th&iacute;ch k&igrave; lạ đ&oacute; của hắn.<br />\r\n<br />\r\nNhưng đối với Hỉ Dương kiếp trước bị từ h&ocirc;n lại c&ograve;n bị cuốn v&agrave;o v&ograve;ng xo&aacute;y tranh đoạt t&igrave;nh y&ecirc;u quyền lực th&igrave; n&agrave;ng chỉ muốn trốn c&agrave;ng xa c&agrave;ng tốt m&agrave; th&ocirc;i, nhưng rồi cuối c&ugrave;ng lại kh&ocirc;ng được như &yacute; nguyện<br />\r\n<br />\r\nĐịch Qu&acirc;n Dương: Phu nh&acirc;n, h&ocirc;m nay c&oacute; thể xem b&oacute;i kh&ocirc;ng?</p>\r\n', 0, '2023-05-31 06:29:20', '2023-05-31 06:29:20'),
(69, 'tu-npc-son-trai-den-dai-boss-bach-cau-dich-the.jpg', 'TỪ NPC SƠN TRẠI ĐẾN ĐẠI BOSS', 'tu-npc-son-trai-den-dai-boss', 1, 'Bạch Câu Dịch Thệ', 'truyenfull.vn', '3', '<p>&quot;Cho d&ugrave; phải l&agrave;m NPC, ta cũng tuyệt đối kh&ocirc;ng l&agrave;m NPC ph&aacute;o h&ocirc;i!&quot;<br />\r\n<br />\r\nTrong Lương Sơn Trại, c&oacute; &quot;người&quot; lặng lẽ một mục ti&ecirc;u!<br />\r\n<br />\r\n...<br />\r\n<br />\r\nTa t&ecirc;n l&agrave; Tần Thư Kiếm.<br />\r\n<br />\r\nSống hoang mang ba mươi năm, văn v&otilde; đều kh&ocirc;ng th&agrave;nh tựu.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; một NPC trong game, một c&acirc;u chuyện từng bước trở th&agrave;nh đại BOSS.</p>\r\n', 0, '2023-05-31 06:32:55', '2023-05-31 06:32:55'),
(70, 'tong-tai-bao-nuoi-chim-hoang-yen-gay-canh-cay-du-nho.jpg', 'TỔNG TÀI BAO NUÔI CHIM HOÀNG YẾN GÃY CÁNH', 'tong-tai-bao-nuoi-chim-hoang-yen-gay-canh', 1, 'Cây Dù Nhỏ', 'truyenfull.vn', '1', '<p><strong>Nh&acirc;n vật:</strong>&nbsp;Thẩm Nhạc Thần x Cố Mộng Diệp, Trịnh Tạ Thi&ecirc;n x Cố Mộng Điệp.<br />\r\n<br />\r\n<strong>Giới thiệu</strong><br />\r\n<br />\r\nThế giới đầu ti&ecirc;n,<br />\r\n<br />\r\nMột tổng gi&aacute;m đốc đi&ecirc;n cuồng, du͙&copy; vọиɠ chiếm hữu cực cao nhưng lại kh&ocirc;ng tự hay biết, sau n&agrave;y trọng sinh, c&agrave;ng ng&agrave;y c&agrave;ng nu&ocirc;ng chiều người kia, n&oacute;i đội l&ecirc;n đầu m&agrave; thờ cũng kh&ocirc;ng ngoa.<br />\r\n<br />\r\nMột diễn vi&ecirc;n kh&ocirc;ng ch&uacute;t tiếng tăm trong gới giải tr&iacute;, l&agrave; người xuy&ecirc;n kh&ocirc;ng, thiếu hụt t&igrave;nh thương, thế n&ecirc;n cực k&igrave; khao kh&aacute;t được y&ecirc;u thương, h&agrave;nh xử, t&acirc;m trạng, tuỳ cảm, c&ograve;n tuỳ thuộc v&agrave;o h&agrave;nh động, cư xử của người kh&aacute;c để đ&aacute;p trả.<br />\r\n<br />\r\n&quot;T&ocirc;i cảm thấy may mắn v&igrave; cậu kh&ocirc;ng đi v&agrave;o giới giải tr&iacute; l&agrave;m diễn vi&ecirc;n đấy, kĩ năng diễn xuất thật tệ hại&quot;. Thẩm Nhạc Thần nh&igrave;n c&aacute;i t&ecirc;n đang rơi lệ trước mặt m&igrave;nh, y b&igrave;nh thản ph&ecirc; b&igrave;nh.<br />\r\n<br />\r\n&quot;...&quot;. Diễn vi&ecirc;n tuyến mười t&aacute;m - Cố Mộng Diệp đang kh&oacute;c cầu sống.<br />\r\n<br />\r\n&quot;Đừng kh&oacute;c nữa, t&ocirc;i kh&ocirc;ng truy cứu cậu nữa&quot;. Thẩm Nhạc Thần phất tay đuổi người như đuổi ch&oacute;, tiếp tục cầm hợp đồng đọc.</p>\r\n', 0, '2023-05-31 06:36:00', '2023-05-31 06:36:00'),
(71, 'cung-chieu-vo-nho-troi-ban-luu-ly-tuyet-tuyet.jpg', 'CƯNG CHIỀU VỢ NHỎ TRỜI BAN', 'cung-chieu-vo-nho-troi-ban', 1, 'Lưu Ly Tuyết Tuyết', 'truyen.one', '3', '<p>Hạ Tịch Qu&aacute;n được gả v&agrave;o U Lan Uyển đ&oacute; l&agrave; một &acirc;m mưu. Hạ Tịch Qu&aacute;n l&agrave; một mỹ nh&acirc;n nghi&ecirc;ng nước nghi&ecirc;ng th&agrave;nh c&ograve;n chồng c&ocirc; th&igrave; ngược lại một người đ&agrave;ng &ocirc;ng với tướng mạo xấu x&iacute;, b&aacute;c sĩ phế t&agrave;i.<br />\r\n<br />\r\n**********<br />\r\n<br />\r\nB&acirc;y giờ, Hạ Tịch Qu&aacute;n thật l&ograve;ng cảm ơn mẹ kế L&yacute; Ngọc Lan của c&ocirc;, L&yacute; Ngọc Lan đ&oacute;n c&ocirc; đến Hải Th&agrave;nh, chỉ cho c&ocirc; ngồi t&agrave;u hỏa gi&aacute; rẻ, nhưng b&agrave; ta lại tổ chức buổi h&ocirc;n lễ cực kỳ xa hoa ho&agrave;nh tr&aacute;ng, muốn gi&agrave;nh được danh tiếng tốt cho b&agrave; ta.<br />\r\n<br />\r\nCon g&aacute;i Hạ gia gả v&agrave;o U Lan Uyển xung hỉ, đ&acirc;y ch&iacute;nh l&agrave; tin được bu&ocirc;n dưa&nbsp;lớn nhất Hải Th&agrave;nh, Hạ Tịch Qu&aacute;n đang đ&aacute;nh cược, đ&aacute;nh cược người đ&agrave;n &ocirc;ng n&agrave;y kh&ocirc;ng muốn chọn phiền to&aacute;i.<br />\r\n<br />\r\nNgười đ&agrave;n &ocirc;ng nh&igrave;n c&ocirc; đầy hứng th&uacute;, h&ocirc;m nay anh bị đ&ocirc;i thủ l&agrave;m ăn thu&ecirc; s&aacute;t thủ giết hại, gặp phải c&ocirc; g&aacute;i n&agrave;y l&agrave; chuyện ngo&agrave;i &yacute; muốn.</p>\r\n', 0, '2023-05-31 06:38:39', '2023-05-31 06:38:39'),
(72, 're-quy-troi-cho-quy-thuong-nhan.jpg', 'RỂ QUÝ TRỜI CHO', 're-quy-troi-cho', 1, 'Quỷ Thượng Nhân', 'truyenfull.vn', '3', '<p>Truyện Rể Qu&yacute; Trời Cho của t&aacute;c giả Quỷ Thượng Nh&acirc;n với nội dung truyện xoay quanh nh&acirc;n vật ch&iacute;nh &ndash; L&acirc;m Thanh Diện: Khi đ&oacute; mẹ L&acirc;m Thanh Diện v&igrave; muốn nắm quyền nh&agrave; họ L&acirc;m m&agrave; kh&ocirc;ng tiếc để anh g&aacute;nh tội danh phản bội, anh phải rời khỏi gia tộc, l&agrave;m con rể phế vật bị người người ch&ecirc; cười ở Giang Th&agrave;nh. Cho đến khi vợ bị ức hiếp, anh mới kh&ocirc;ng thể tiếp tục điệu thấp nữa, lộ th&acirc;n phận, vả mặt bọn người.</p>\r\n', 0, '2023-05-31 06:41:26', '2023-05-31 06:41:26'),
(73, 'linh-vu-thien-ha-vu-phong.jpg', 'LINH VŨ THIÊN HẠ', 'linh-vu-thien-ha', 1, 'Vũ Phong', 'toptruyen.com', '3', '<p><strong>Lục Thiếu Du</strong>, linh hồn bị xuy&ecirc;n qua đến thế giới kh&aacute;c, nhập v&agrave;o th&acirc;n thể của một thiếu gia kh&ocirc;ng c&oacute; địa vị phải trải qua cuộc sống kh&ocirc;ng kh&aacute;c g&igrave; n&ocirc; bộc.<br />\r\nThế giới n&agrave;y lấy Vũ vi cường, lấy Linh vi t&ocirc;n, nghe đồn khi v&otilde; đạo đỉnh phong, linh đạo đạt đến cực hạn c&oacute; thể ph&aacute; to&aacute;i hư kh&ocirc;ng.<br />\r\nLục Thiếu Du mang theo k&yacute; ức từ kiếp trước t&aacute;i sinh, kh&ocirc;ng cam l&ograve;ng l&agrave;m một thiếu gia chẳng kh&aacute;c g&igrave; củi mục.<br />\r\n<br />\r\nTrong thế giới xa lạ,<br />\r\nSay - nằm tr&ecirc;n gối mỹ nh&acirc;n,<br />\r\nTỉnh - nắm quyền thi&ecirc;n hạ.<br />\r\nĐ&acirc;y mới l&agrave; cuộc sống đ&aacute;ng mơ ước.<br />\r\nLinh - Vũ song tu<br />\r\nB&aacute; chủ ki&ecirc;u h&ugrave;ng<br />\r\nĐ&atilde; đến, ta sẽ lưu lại một huyền thoại......<br />\r\n<br />\r\n<strong><strong>Hệ Thống Tu Luyện:</strong></strong><br />\r\n<br />\r\n<strong>- Vũ giả:&nbsp;</strong>Vũ kh&iacute;, Vũ đồ, Vũ sĩ, Vũ sư, Vũ ph&aacute;ch, Vũ tướng, Vũ suất, Vũ vương, Vũ t&ocirc;n, Vũ đế.<br />\r\n<strong>- Linh giả:&nbsp;</strong>Linh kh&iacute;, Linh đồ, Linh sĩ, Linh sư, Linh ph&aacute;ch, Linh tướng, Linh suất, Linh vương, Linh t&ocirc;n, Linh đế.<br />\r\n<br />\r\nMỗi cấp được chia l&agrave;m cửu trọng (ch&iacute;n tầng). Ngo&agrave;i ra c&ograve;n c&oacute; Y&ecirc;u th&uacute;, Linh th&uacute; c&aacute;c thể loại, chia l&agrave;m nhất giai, nhị giai,... mỗi giai chia l&agrave;m sơ kỳ, trung kỳ, hậu kỳ.<br />\r\n<br />\r\n-&nbsp;<strong>C&ocirc;ng ph&aacute;p:</strong>&nbsp;chia l&agrave;m s&aacute;u loại, từ cao đến thấp:&nbsp;Thi&ecirc;n, Địa, Huyền, Ho&agrave;ng, Tinh, Thần<br />\r\n<br />\r\nTất nhi&ecirc;n l&agrave; vẫn tồn tại một số c&ocirc;ng ph&aacute;p đặc biệt b&iacute; ẩn kh&aacute;c.<br />\r\n<br />\r\n-&nbsp;<strong>Ngũ hệ:&nbsp;</strong>Vũ giả, Vũ kỹ (c&ocirc;ng ph&aacute;p cho vũ giả) được chia th&agrave;nh năm loại Mộc, Thủy, Hỏa, Thổ, Phong.</p>\r\n', 0, '2023-05-31 06:43:34', '2023-05-31 06:43:34'),
(74, 'diu-dang-tan-xuong-tay-phuong-kinh-te-hoc.jpg', 'DỊU DÀNG TẬN XƯƠNG', 'diu-dang-tan-xuong', 1, 'Tây Phương Kinh Tế Học', 'toptruyen.com', '2', '<p>Khương C&aacute;ch mang vẻ đẹp lạnh l&ugrave;ng, t&iacute;nh t&igrave;nh l&atilde;nh đạm, l&agrave; mỹ nh&acirc;n băng gi&aacute; nổi tiếng trong giới giải tr&iacute;. Năm ấy c&ocirc; ra mắt bằng một bức ảnh chụp c&ugrave;ng với b&aacute;o hoa mai.<br />\r\n<br />\r\nTrong khung cảnh c&aacute;t bụi đầy trời, dưới một gốc c&acirc;y kh&ocirc;, thiếu nữ kho&aacute;c tấm lụa đỏ, đứng đối diện con b&aacute;o hoa mai to&agrave;n th&acirc;n phủ đốm. &Aacute;nh mắt c&ocirc; lạnh l&ugrave;ng xa c&aacute;ch, &aacute;nh mắt con b&aacute;o đề ph&ograve;ng nguy hiểm.<br />\r\n<br />\r\nThời điểm chụp tấm ảnh ấy, Khương C&aacute;ch vừa tr&ograve;n mười t&aacute;m tuổi.<br />\r\n<br />\r\nNăm ấy, c&ocirc; gặp Qu&yacute; Tranh.</p>\r\n', 0, '2023-05-31 06:48:36', '2023-05-31 06:48:36'),
(75, 'sao-bang-khong-xuat-hien-jinruan.jpg', 'SAO BĂNG KHÔNG XUẤT HIỆN', 'sao-bang-khong-xuat-hien', 1, 'JinRuan', 'truyenfull.vn', '3', '<p>Nghe n&oacute;i đ&ecirc;m nay sẽ c&oacute; sao băng, tr&ugrave;ng hợp, Hải Uy&ecirc;n cũng đang c&oacute; một mong ước.<br />\r\n<br />\r\n&quot;Muốn vừa thực hiện ước mơ của ba mẹ, vừa thỏa m&atilde;n đam m&ecirc; của bản th&acirc;n? N&agrave;ng Qu&yacute;t, c&ocirc; cũng tham lam qu&aacute; đấy. Nhưng m&agrave; c&oacute; một c&aacute;ch, học song song cả hai chuy&ecirc;n ng&agrave;nh, c&ocirc; thấy sao?&quot;<br />\r\n<br />\r\nHải Uy&ecirc;n kh&ocirc;ng biết rằng tr&ecirc;n đời n&agrave;y kh&ocirc;ng c&oacute; tr&ugrave;ng hợp, chỉ c&oacute; sắp đặt. Một, do người, gọi l&agrave; kế. Hai, do trời, gọi l&agrave; mệnh.<br />\r\n<br />\r\nĐịnh mệnh sắp đặt Hải Uy&ecirc;n gặp được Hồng Kh&aacute;nh, th&iacute;ch anh, y&ecirc;u anh. V&agrave; cũng định mệnh sắp đặt, khi đang đắm ch&igrave;m trong vị ngọt của t&igrave;nh y&ecirc;u th&igrave; bỗng nhi&ecirc;n ph&aacute;t hiện, bạn trai m&igrave;nh l&agrave; một tra nam.<br />\r\n<br />\r\n&quot;Hồng Kh&aacute;nh, anh kh&ocirc;ng nghe r&otilde; hay đang giả điếc đấy? T&ocirc;i n&oacute;i t&ocirc;i ch&aacute;n anh rồi, t&ocirc;i muốn t&igrave;m người mới.&quot;<br />\r\n<br />\r\nMột lần gặp được sao băng l&agrave; một lần điều ước th&agrave;nh sự thật, vậy n&ecirc;n mới mơ tưởng, chỉ cần bản th&acirc;n th&agrave;nh t&acirc;m cầu nguyện, tất cả mọi điều ước đều sẽ th&agrave;nh hiện thực.<br />\r\n<br />\r\n&quot;Gh&eacute;t anh như thế n&agrave;o? T&ocirc;i cầu ngay l&uacute;c n&agrave;y c&oacute; sao băng bay qua, để ước m&igrave;nh chưa từng gặp phải anh.&quot;<br />\r\n<br />\r\n&quot;Nhưng m&agrave;..sao băng kh&ocirc;ng xuất hiện.&quot;</p>\r\n', 0, '2023-05-31 06:48:13', '2023-05-31 06:48:13'),
(76, 'chiec-bat-lua-va-vay-cong-chua-twentine.jpg', 'CHIẾC BẬT LỬA VÀ VÁY CÔNG CHÚA', 'chiec-bat-lua-va-vay-cong-chua', 0, 'Twentine', 'hanvuphi.wordpress.com', '3', '<p>T&ocirc;i l&agrave; bề t&ocirc;i trung th&agrave;nh của anh, Quốc vương của t&ocirc;i!<br />\r\n<br />\r\nV&igrave; anh t&ocirc;i c&oacute; thể phất cờ cổ vũ, nguyện c&ugrave;ng anh m&agrave; chinh chiến nơi sa trường.</p>\r\n', 0, '2023-05-31 06:52:48', '2023-05-31 06:52:48'),
(77, 'e-qua-roi,-mau-lay-chong-thoi!-lan-rua.jpg', 'Ế QUÁ RỒI, MAU LẤY CHỒNG THÔI!', 'e-qua-roi,-mau-lay-chong-thoi!', 0, 'Lan Rùa', 'truyenfull.vn', '3', '<p>Bạn đang đọc truyện&nbsp;<strong>Ế Qu&aacute; Rồi, Mau Lấy Chồng Th&ocirc;i!</strong>&nbsp;của t&aacute;c giả&nbsp;<strong>Lan R&ugrave;a</strong>. Trai lớn lấy vợ, g&aacute;i lớn gả chồng.<br />\r\n<br />\r\nTừ trước tới nay, quan niệm n&agrave;y vẫn chưa từng thay đổi.<br />\r\n<br />\r\nM&agrave; khi độ tuổi tươi đẹp nhất đ&atilde; qua, họ th&agrave;nh những người qu&aacute; lứa lỡ th&igrave;.<br />\r\n<br />\r\nY&ecirc;u đương cũng c&oacute;, nhưng lại kh&ocirc;ng thể nắm tay đi đến h&ocirc;n nh&acirc;n.<br />\r\n<br />\r\nAi cũng c&oacute; qu&aacute; khứ ri&ecirc;ng, những kỉ niệm đau thương, m&agrave; gặp được nhau, quan t&acirc;m, cảm th&ocirc;ng nhau, c&oacute; lẽ mới l&agrave; điều khiến cả hai cảm thấy thỏa m&atilde;n nhất.</p>\r\n', 0, '2023-05-31 06:54:23', '2023-05-31 06:54:23'),
(78, 'bach-khoa-yeu-thuong-lan-rua.jpg', 'BÁCH KHOA YÊU THƯƠNG', 'bach-khoa-yeu-thuong', 0, 'Lan Rùa', 'truyenfull.vn', '3', '<p>Truyện kể về bốn c&ocirc; g&aacute;i, về&nbsp;cuộc sống đủ thứ chuyện tr&ecirc;n trời dưới đất thời sinh vi&ecirc;n dưới m&aacute;i trường th&acirc;n y&ecirc;u của c&aacute;c c&ocirc;. V&igrave; t&aacute;c giả rất thương người... ngốc n&ecirc;n thỉnh thoảng sẽ hơi sa đ&agrave; v&agrave;o bạn ngốc ấy khiến c&aacute;c bạn kia bớt đất diễn. Truyện c&oacute; xu hướng&nbsp;<em>hơi&nbsp;ảo tưởng</em>, tr&ecirc;n tinh thần giải tr&iacute; l&agrave; ch&iacute;nh.<br />\r\n<br />\r\nTruyện kh&ocirc;ng c&oacute; lịch ra chương cụ thể, dự đo&aacute;n l&agrave; một tuần hoặc nửa th&aacute;ng mới c&oacute; chương mới, mọi người xem x&eacute;t kỹ trước khi nhảy hố.</p>\r\n', 0, '2023-05-31 06:55:42', '2023-05-31 06:55:42');

